/*---------------*/
END IF;

IF(OLD.enrollment_date <> NEW.enrollment_date) THEN
/*---------------*/
/*Study subject value changed*/
SELECT INTO pk NEXTVAL(''audit_log_event_audit_id_seq'');
SELECT INTO entity_name_value ''Enrollment Date'';
