VALUES (pk, ''27'', now(), NEW.update_id, ''study_subject'', NEW.study_subject_id, entity_name_value, old_unique_identifier, new_unique_identifier);
/*---------------*/
END IF;

RETURN NULL;  /*return values ignored for ''after'' triggers*/
END IF;
END;
'
LANGUAGE plpgsql;

